INSERT INTO sections (section_type, section_value, resume_uuid) VALUES ('PERSONAL','{"CLASSNAME":"kz.akbar.basejava.model.TextSection","INSTANCE":{"description":"Аналитический склад ума, сильная логика, креативность, инициативность. Пурист кода и архитектуры."}}','5b76ee32-d12d-4433-af79-e85803b54b65');
INSERT INTO sections (section_type, section_value, resume_uuid) VALUES ('ACHIEVEMENTS','{"CLASSNAME":"kz.akbar.basejava.model.ListSection","INSTANCE":{"items":["С 2013 года: разработка проектов \"Разработка Web приложения\",\"Java Enterprise\", \"Многомодульный maven. Многопоточность. XML (JAXB/StAX). Веб сервисы (JAX-RS/SOAP). Удаленное взаимодействие (JMS/AKKA)\". Организация онлайн стажировок и ведение проектов. Более 1000 выпускников.","Реализация двухфакторной аутентификации для онлайн платформы управления проектами Wrike. Интеграция с Twilio, DuoSecurity, Google Authenticator, Jira, Zendesk.","Налаживание процесса разработки и непрерывной интеграции ERP системы River BPM. Интеграция с 1С, Bonita BPM, CMIS, LDAP. Разработка приложения управления окружением на стеке: Scala/Play/Anorm/JQuery. Разработка SSO аутентификации и авторизации различных ERP модулей, интеграция CIFS/SMB java сервера.","Реализация c нуля Rich Internet Application приложения на стеке технологий JPA, Spring, Spring-MVC, GWT, ExtGWT (GXT), Commet, HTML5, Highstock для алгоритмического трейдинга.","Создание JavaEE фреймворка для отказоустойчивого взаимодействия слабо-связанных сервисов (SOA-base архитектура, JAX-WS, JMS, AS Glassfish). Сбор статистики сервисов и информации о состоянии через систему мониторинга Nagios. Реализация онлайн клиента для администрирования и мониторинга системы по JMX (Jython/ Django).","Реализация протоколов по приему платежей всех основных платежных системы России (Cyberplat, Eport, Chronopay, Сбербанк), Белоруcсии(Erip, Osmp) и Никарагуа."]}}','5b76ee32-d12d-4433-af79-e85803b54b65');
INSERT INTO sections (section_type, section_value, resume_uuid) VALUES ('QUALIFICATIONS','{"CLASSNAME":"kz.akbar.basejava.model.ListSection","INSTANCE":{"items":["JEE AS: GlassFish (v2.1, v3), OC4J, JBoss, Tomcat, Jetty, WebLogic, WSO2.","Version control: Subversion, Git, Mercury, ClearCase, Perforce.","DB: PostgreSQL(наследование, pgplsql, PL/Python), Redis (Jedis), H2, Oracle.","MySQL, SQLite, MS SQL, HSQLDB.","Languages: Java, Scala, Python/Jython/PL-Python, JavaScript, Groovy.","XML/XSD/XSLT, SQL, C/C++, Unix shell scripts.","Java Frameworks: Java 8 (Time API, Streams), Guava, Java Executor, MyBatis, Spring (MVC, Security, Data, Clouds, Boot), JPA (Hibernate, EclipseLink), Guice, GWT(SmartGWT, ExtGWT/GXT), Vaadin, Jasperreports, Apache Commons, Eclipse SWT, JUnit, Selenium (htmlelements).","Python: Django.","JavaScript: jQuery, ExtJS, Bootstrap.js, underscore.js."]}}','5b76ee32-d12d-4433-af79-e85803b54b65');
INSERT INTO sections (section_type, section_value, resume_uuid) VALUES ('EXPERIENCE','{"CLASSNAME":"kz.akbar.basejava.model.OrganizationSection","INSTANCE":{"organizations":[{"nameLink":{"title":"Java Online Projects","value":""},"positions":[{"startDate":{"year":2013,"month":10,"day":1},"endDate":{"year":9999,"month":12,"day":1},"title":"Автор проекта","description":"Создание, организация и проведение Java онлайн проектов и стажировок."}]},{"nameLink":{"title":"Wrike","value":""},"positions":[{"startDate":{"year":2014,"month":10,"day":1},"endDate":{"year":2016,"month":1,"day":1},"title":"Старший разработчик (backend)","description":"Проектирование и разработка онлайн платформы управления проектами Wrike (Java 8 API, Maven, Spring, MyBatis, Guava, Vaadin, PostgreSQL, Redis). Двухфакторная аутентификация, авторизация по OAuth1, OAuth2, JWT SSO."}]},{"nameLink":{"title":"RIT Center","value":""},"positions":[{"startDate":{"year":2012,"month":4,"day":1},"endDate":{"year":2014,"month":10,"day":1},"title":"Java архитектор","description":"Организация процесса разработки системы ERP для разных окружений: релизная политика, версионирование, ведение CI (Jenkins миграция базы (кастомизация Flyway конфигурирование системы (pgBoucer, Nginx AAA via SSO. Архитектура БД и серверной части системы. Разработка интергационных сервисов: CMIS, BPMN2, 1C (WebServices сервисов общего назначения (почта, экспорт в pdf, doc, html). Интеграция Alfresco JLAN для online редактирование из браузера документов MS Office. Maven + plugin development, Ant, Apache Commons, Spring security, Spring MVC, Tomcat,WSO2, xcmis, OpenCmis, Bonita, Python scripting, Unix shell remote scripting via ssh tunnels, PL/Python."}]},{"nameLink":{"title":"Luxoft","value":""},"positions":[{"startDate":{"year":2010,"month":12,"day":1},"endDate":{"year":2012,"month":4,"day":1},"title":"Ведущий программист","description":"Участие в проекте Deutsche Bank CRM (WebLogic, Hibernate, Spring, Spring MVC, SmartGWT, GWT, Jasper, Oracle). Реализация клиентской и серверной части CRM. Реализация RIA-приложения для администрирования, мониторинга и анализа результатов в области алгоритмического трейдинга. JPA, Spring, Spring-MVC, GWT, ExtGWT (GXT Highstock, Commet, HTML5."}]},{"nameLink":{"title":"Yota","value":""},"positions":[{"startDate":{"year":2008,"month":6,"day":1},"endDate":{"year":2010,"month":12,"day":1},"title":"Ведущий специалист","description":"Дизайн и имплементация Java EE фреймворка для отдела \"Платежные Системы\" (GlassFish v2.1, v3, OC4J, EJB3, JAX-WS RI 2.1, Servlet 2.4, JSP, JMX, JMS, Maven2). Реализация администрирования, статистики и мониторинга фреймворка. Разработка online JMX клиента (Python/ Jython, Django, ExtJS)."}]},{"nameLink":{"title":"Enkata","value":""},"positions":[{"startDate":{"year":2007,"month":3,"day":1},"endDate":{"year":2008,"month":6,"day":1},"title":"Разработчик ПО","description":"Реализация клиентской (Eclipse RCP) и серверной (JBoss 4.2, Hibernate 3.0, Tomcat, JMS) частей кластерного J2EE приложения (OLAP, Data mining)."}]},{"nameLink":{"title":"Siemens AG","value":""},"positions":[{"startDate":{"year":2005,"month":1,"day":1},"endDate":{"year":2007,"month":2,"day":1},"title":"Разработчик ПО","description":"Разработка информационной модели, проектирование интерфейсов, реализация и отладка ПО на мобильной IN платформе Siemens @vantage (Java, Unix)."}]},{"nameLink":{"title":"Alcatel","value":""},"positions":[{"startDate":{"year":1997,"month":9,"day":1},"endDate":{"year":2005,"month":1,"day":1},"title":"Инженер по аппаратному и программному тестированию","description":"Тестирование, отладка, внедрение ПО цифровой телефонной станции Alcatel 1000 S12 (CHILL, ASM)."}]}]}}','5b76ee32-d12d-4433-af79-e85803b54b65');
INSERT INTO sections (section_type, section_value, resume_uuid) VALUES ('EDUCATION','{"CLASSNAME":"kz.akbar.basejava.model.OrganizationSection","INSTANCE":{"organizations":[{"nameLink":{"title":"Coursera","value":""},"positions":[{"startDate":{"year":2013,"month":3,"day":1},"endDate":{"year":2013,"month":5,"day":1},"title":"Слушатель","description":"Курс \"Functional Programming Principles in Scala\" by Martin Odersky\""}]},{"nameLink":{"title":"Luxoft","value":""},"positions":[{"startDate":{"year":2011,"month":3,"day":1},"endDate":{"year":2011,"month":4,"day":1},"title":"Слушатель","description":"Курс \"Объектно-ориентированный анализ ИС. Концептуальное моделирование на UML\""}]},{"nameLink":{"title":"Siemens AG","value":""},"positions":[{"startDate":{"year":2005,"month":1,"day":1},"endDate":{"year":2005,"month":4,"day":1},"title":"Практикант","description":"3 месяца обучения мобильным IN сетям (Берлин)"}]},{"nameLink":{"title":"Alcatel","value":""},"positions":[{"startDate":{"year":1997,"month":9,"day":1},"endDate":{"year":1998,"month":3,"day":1},"title":"Практикант","description":"6 месяцев обучения цифровым телефонным сетям (Москва)"}]},{"nameLink":{"title":"Санкт-Петербургский национальный исследовательский университет информационных технологий, механики и оптики","value":""},"positions":[{"startDate":{"year":1993,"month":9,"day":1},"endDate":{"year":1996,"month":7,"day":1},"title":"Аспирант","description":"Аспирантура (программист С, С++)"},{"startDate":{"year":1987,"month":9,"day":1},"endDate":{"year":1993,"month":7,"day":1},"title":"Студент","description":"Инженер (программист Fortran, C)"}]},{"nameLink":{"title":"Заочная физико-техническая школа при МФТИ","value":""},"positions":[{"startDate":{"year":1984,"month":9,"day":1},"endDate":{"year":1987,"month":6,"day":1},"title":"Школьник","description":"Закончил с отличием"}]}]}}','5b76ee32-d12d-4433-af79-e85803b54b65');
